import React, { useState, useRef, useEffect } from "react";
import { searchFilter } from "./Filter";
import "./style.css";

const list = [
  { id: 1, name: "Tom", type: "tom@gmail.com, +91999999999" },
  { id: 2, name: "Christopher", type: "christopher@gmail.com, +918888888888" },
  { id: 3, name: "Luke", type: "luke@gmail.com, +917777777777" },
  { id: 4, name: "James", type: "james@gmail.com, +916666666666" },
  { id: 5, name: "Harry", type: "harry@gmail.com, +915555555555" },
  { id: 6, name: "Jerry", type: "jerry@gmail.com, +919494949494" },
  { id: 7, name: "Hanson", type: "hanson@gmail.com, +9393939393" },
  { id: 8, name: "Harry", type: "harry@gmail.com, +912929292929" },
  { id: 9, name: "Leonard", type: "leonard@gmail.com, +911919191919" }
];

const DropdownItems = () => {
  const [visible, setVisible] = useState(false);
  const [searchValue, setSearchValue] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);
  const dropdownRef = useRef(null);

  // click away listener
  useEffect(() => {
    document.addEventListener("mousedown", handleClick, false);
    return () => document.removeEventListener("mousedown", handleClick, false);
  }, []);

  const handleClick = (e) => {
    if (dropdownRef.current.contains(e.target)) {
      return;
    }
    setVisible(false);
  };

  const handleChange = (e) => {
    setSearchValue(e.target.value);
    if (!visible) {
      setVisible(true);
    }
  };

  const selectItem = (item) => {
    setSearchValue(item.name);
    setSelectedItem(item.id);
    setVisible(false);
  };

  const selectChange = (e) => {
    console.log(e.target.value);
  };
  return (
    <div className="container">
      <div tabIndex="0" className="input_container">
        <div className="main-text">
          You can also invite upto three people to join
        </div>
        <br />
        <input
          className="input"
          type="text"
          placeholder="Search members in your contact"
          value={searchValue}
          onChange={handleChange}
          onFocus={() => {
            // if (searchValue) {
            setVisible(true);
            // };
          }}
        />
      </div>

      <div ref={dropdownRef} className={`dropdown ${visible ? "v" : ""}`}>
        {visible && (
          <ul>
            {!list && (
              <li key="zxc" className="dropdown_item">
                no result
              </li>
            )}
            {/* you can remove the searchFilter if you get results from Filtered API like Google search */}
            {list &&
              searchFilter(searchValue, list).map((x) => (
                <li
                  key={x.id}
                  onClick={() => selectItem(x)}
                  className="dropdown_item"
                >
                  <div className="item_text1">{x.name}</div>
                  <div className="item_text2">{x.type}</div>
                  <div className="item_image"> {x.image}</div>
                </li>
              ))}
          </ul>
        )}
      </div>
      <br />
      <div class="buttons">
        <button class="button" type="button">
          Next
        </button>
      </div>
      <br />
      <br />
      <div className="small-text">
        <strong>skip this step here </strong>and invite people later from the
        user profile.
      </div>
    </div>
  );
};

export default DropdownItems;
